import React from 'react';
import './App.css'; // Ensure this CSS file contains the necessary styles

const DineWithUs = () => {
    return (
        <div>
            <h1>Dine With Us</h1>
            <div id="dine-with-us" className="woodroffe-section">
                <p className="woodroffe-description">
                    Our culinary philosophy is centered on a farm-to-fork ethos, championed by our head chef's dedication to fresh, locally sourced ingredients. Celebrated for a menu that harmonises traditional techniques with modern gastronomic innovation, the kitchen at The Woodroffe is the beating heart of our establishment, pulsing with a passion for quality and a reverence for the bounty of the surrounding Peak District.
                    <br /><br />
                    Within the warm glow of our dining room, framed by wooden beams and stonework that whispers tales of yesteryear, each meal is an intimate affair. The aroma of fresh herbs and the sizzle of premium cuts on the grill complement the clink of glasses, as patrons from near and far gather to enjoy the simple pleasures of a meal well-made.
                    <br /><br />
                    Whether you're settling in for a leisurely breakfast, a relaxed lunch, or an indulgent dinner, our chef and their team are ready to serve you a plate of the Peak District, prepared with care and served with pride. At The Woodroffe Arms, we don't just serve food; we celebrate the art of good eating.
                </p>
                <img src="/gallery/WhatsOn.jpg" alt="Whats On" className="woodroffe-image woodroffe-image-reduced" />
            </div>
        </div>
    );
};

export default DineWithUs;
