import React from 'react';
import './App.css'; // Ensure this CSS file contains the necessary styles

const DineWithUs = () => {
    return (
        <div>
            <h1>Dine With Us</h1>
            <div id="dine-with-us" className="woodroffe-section">
                <p className="woodroffe-description">
                    Embark on a culinary journey at The Woodroffe Arms, nestled in the heart of the picturesque Peak District. Each dish we serve tells a story of the region's rich heritage and bountiful lands, brought to life by the skilled hands of our talented chefs. Our menu celebrates the locally-sourced, seasonal ingredients that are transformed into mouthwatering experiences. From hearty, home-cooked meals to our exquisite Sunday carvery, every bite showcases our unwavering commitment to quality and flavor.
                    <br /><br />
                    A cornerstone of our culinary philosophy is the close collaboration between our chefs and local farmers and producers. This partnership ensures that only the freshest and finest ingredients find their way into our kitchen and onto your plate. Embracing local sourcing not only supports the surrounding community but also reduces our environmental footprint, enabling us to present dishes that are both delicious and sustainably responsible.
                </p>
                <img src="/gallery/WhatsOn.jpg" alt="Dining Room" className="woodroffe-image" />
            </div>
        </div>
    );
};

export default DineWithUs;
